    <script>
        document.addEventListener("DOMContentLoaded", () => {
            const humberger = document.getElementById("humberger");
            const sidebar = document.getElementById("sidebar");
            humberger.addEventListener("click", (e) => {
                sidebar.classList.toggle('-ml-64');
            });
            
            const humbergerMobile = document.getElementById("humberger-mobile");
            const navMobile = document.getElementById("nav-mobile");
            function toggleNav() {
                navMobile.classList.toggle("translate-x-full");
            }

            humbergerMobile.addEventListener("click", (e) => {
                e.stopPropagation();
                toggleNav();
            });

            document.addEventListener("click", (e) => {
                if (
                    !navMobile.classList.contains("translate-x-full") && 
                    !navMobile.contains(e.target) && 
                    !humbergerMobile.contains(e.target) 
                ) {
                    toggleNav();
                }
            });

            navMobile.querySelectorAll("a, button").forEach((el) => {
                el.addEventListener("click", () => {
                    if (!navMobile.classList.contains("translate-x-full")) {
                        toggleNav();
                    }
                });
            });

            @if(session('success'))
                modal.alert({
                    message: '{{ session('success') }}',
                    type: 'success'
                });
            @endif

            @if(session('error'))
                modal.alert({
                    message: '{{ session('error') }}',
                    type: 'error'
                });
            @endif
        });
    </script>
